import { useState } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { Loader2, Trophy, Target } from "lucide-react";
import { AchievementUnlockNotification } from "@/components/achievement-unlock-notification";

interface Question {
  question: string;
  options: string[];
  correctAnswer: number;
}

interface QuizModalProps {
  sessionId: string;
  onClose: () => void;
}

export function QuizModal({ sessionId, onClose }: QuizModalProps) {
  const { toast } = useToast();
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState<Record<number, number>>({});
  const [showResults, setShowResults] = useState(false);
  const [quizScore, setQuizScore] = useState<{ score: number; total: number } | null>(null);
  const [newAchievements, setNewAchievements] = useState<string[]>([]);

  // Fetch quiz questions
  const { data: quizData, isLoading } = useQuery<{ questions: Question[] }>({
    queryKey: ["/api/quiz", sessionId],
    retry: false,
  });

  // Submit quiz mutation
  const submitMutation = useMutation({
    mutationFn: async (answers: Record<number, number>) => {
      const response = await apiRequest("POST", `/api/quiz/${sessionId}/submit`, {
        answers,
      });
      const result = await response.json();
      console.log("Quiz submission response:", result);
      return result;
    },
    onSuccess: (data: any) => {
      console.log("Quiz onSuccess data:", data);
      console.log("data.score:", data.score);
      console.log("data.totalQuestions:", data.totalQuestions);
      const score = Number(data.score) || 0;
      const total = Number(data.totalQuestions) || 1;
      console.log("Parsed score:", score, "total:", total);
      setQuizScore({ score, total });
      setShowResults(true);
      if (data.newAchievements && data.newAchievements.length > 0) {
        setNewAchievements(data.newAchievements);
      }
      queryClient.invalidateQueries({ queryKey: ["/api/sessions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/activity/me"] });
      queryClient.invalidateQueries({ queryKey: ["/api/streak"] });
      queryClient.invalidateQueries({ queryKey: ["/api/leaderboard"] });
      queryClient.invalidateQueries({ queryKey: ["/api/achievements/me"] });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to submit quiz. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleAnswerSelect = (questionIndex: number, answerIndex: number) => {
    setSelectedAnswers((prev) => ({
      ...prev,
      [questionIndex]: answerIndex,
    }));
  };

  const handleNext = () => {
    if (quizData && currentQuestionIndex < quizData.questions.length - 1) {
      setCurrentQuestionIndex((prev) => prev + 1);
    }
  };

  const handlePrevious = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex((prev) => prev - 1);
    }
  };

  const handleSubmit = () => {
    submitMutation.mutate(selectedAnswers);
  };

  const getEncouragingMessage = (percentage: number) => {
    if (percentage === 100) return "Perfect score! Outstanding! ";
    if (percentage >= 80) return "Excellent work! Keep it up! ";
    if (percentage >= 60) return "Good job! You're making progress! ";
    if (percentage >= 40) return "Nice try! Review and try again! ";
    return "Keep learning! Every attempt makes you better! ";
  };

  if (isLoading) {
    return (
      <Dialog open onOpenChange={onClose}>
        <DialogContent className="max-w-2xl" data-testid="dialog-quiz">
          <DialogHeader>
            <DialogTitle>Generating Quiz</DialogTitle>
          </DialogHeader>
          <div className="flex flex-col items-center justify-center py-12">
            <Loader2 className="w-12 h-12 animate-spin text-primary mb-4" />
            <p className="text-lg font-medium">Generating your quiz...</p>
            <p className="text-sm text-muted-foreground">AI is creating personalized questions</p>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  if (!quizData || !quizData.questions || quizData.questions.length === 0) {
    return (
      <Dialog open onOpenChange={onClose}>
        <DialogContent data-testid="dialog-quiz-error">
          <DialogHeader>
            <DialogTitle>Quiz Generation Failed</DialogTitle>
          </DialogHeader>
          <div className="py-6 text-center">
            <p className="text-muted-foreground mb-4">
              We couldn't generate questions for this topic. Please try again.
            </p>
            <Button onClick={onClose}>Close</Button>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  const currentQuestion = quizData.questions[currentQuestionIndex];
  const totalQuestions = quizData.questions.length;
  const allAnswered = Object.keys(selectedAnswers).length === totalQuestions;

  return (
    <>
      {newAchievements.length > 0 && (
        <AchievementUnlockNotification
          achievementIds={newAchievements}
          onClose={() => setNewAchievements([])}
        />
      )}
      <Dialog open onOpenChange={onClose}>
        <DialogContent className="max-w-2xl" data-testid="dialog-quiz">
          {showResults && quizScore ? (
          // Results View
          <>
            <DialogHeader>
              <DialogTitle>Quiz Complete!</DialogTitle>
            </DialogHeader>
            <div className="py-8 text-center">
              <div className="w-20 h-20 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-4">
                <Trophy className="w-10 h-10 text-primary" />
              </div>
              <div className="text-6xl font-extrabold text-primary my-6" data-testid="text-quiz-score">
                {quizScore.total > 0 ? Math.round((quizScore.score / quizScore.total) * 100) : 0}%
              </div>
              <p className="text-lg mb-2">
                {quizScore.score} out of {quizScore.total} correct
              </p>
              <p className="text-muted-foreground mb-8">
                {getEncouragingMessage(quizScore.total > 0 ? (quizScore.score / quizScore.total) * 100 : 0)}
              </p>
              <Button onClick={onClose} size="lg" data-testid="button-close-results">
                Continue
              </Button>
            </div>
            </>
          ) : (
            // Quiz View
            <>
            <DialogHeader>
              <DialogTitle className="flex items-center justify-between">
                <span>Question {currentQuestionIndex + 1} of {totalQuestions}</span>
                <div className="flex gap-1">
                  {Array.from({ length: totalQuestions }).map((_, idx) => (
                    <div
                      key={idx}
                      className={`w-2 h-2 rounded-full ${
                        idx in selectedAnswers
                          ? "bg-primary"
                          : "bg-muted"
                      }`}
                    />
                  ))}
                </div>
              </DialogTitle>
            </DialogHeader>

            <div className="py-6">
              <h3 className="text-xl font-semibold mb-6" data-testid="text-question">
                {currentQuestion.question}
              </h3>

              <RadioGroup
                value={selectedAnswers[currentQuestionIndex]?.toString()}
                onValueChange={(value) =>
                  handleAnswerSelect(currentQuestionIndex, parseInt(value))
                }
              >
                <div className="space-y-3">
                  {currentQuestion.options.map((option, idx) => (
                    <div
                      key={idx}
                      className={`border-2 rounded-lg p-4 cursor-pointer transition-all ${
                        selectedAnswers[currentQuestionIndex] === idx
                          ? "border-primary bg-primary/10"
                          : "border-border hover:border-primary/50 hover:bg-primary/5"
                      }`}
                      onClick={() => handleAnswerSelect(currentQuestionIndex, idx)}
                      data-testid={`option-${idx}`}
                    >
                      <div className="flex items-center gap-3">
                        <RadioGroupItem value={idx.toString()} id={`option-${idx}`} />
                        <Label
                          htmlFor={`option-${idx}`}
                          className="flex-1 cursor-pointer text-base"
                        >
                          {option}
                        </Label>
                      </div>
                    </div>
                  ))}
                </div>
              </RadioGroup>
            </div>

            <div className="flex gap-3 pt-4 border-t">
              <Button
                variant="outline"
                onClick={handlePrevious}
                disabled={currentQuestionIndex === 0}
                className="flex-1"
                data-testid="button-previous"
              >
                Previous
              </Button>
              {currentQuestionIndex < totalQuestions - 1 ? (
                <Button
                  onClick={handleNext}
                  disabled={!(currentQuestionIndex in selectedAnswers)}
                  className="flex-1"
                  data-testid="button-next"
                >
                  Next
                </Button>
              ) : (
                <Button
                  onClick={handleSubmit}
                  disabled={!allAnswered || submitMutation.isPending}
                  className="flex-1"
                  data-testid="button-submit-quiz"
                >
                  {submitMutation.isPending ? "Submitting..." : "Submit Quiz"}
                </Button>
              )}
            </div>
            </>
          )}
        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/pages/leaderboard.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Trophy, Flame, Medal } from "lucide-react";
import { UserProgressModal } from "@/components/user-progress-modal";

interface LeaderboardUser {
  id: string;
  firstName: string | null;
  lastName: string | null;
  profileImageUrl: string | null;
  totalScore: number;
  streak: number;
}

export default function Leaderboard() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [selectedUserId, setSelectedUserId] = useState<string | null>(null);

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  // Fetch leaderboard data
  const { data: leaderboard = [] } = useQuery<LeaderboardUser[]>({
    queryKey: ["/api/leaderboard"],
    enabled: isAuthenticated,
  });

  const getMedalIcon = (rank: number) => {
    if (rank === 1) return <Medal className="w-5 h-5 text-yellow-500" />;
    if (rank === 2) return <Medal className="w-5 h-5 text-gray-400" />;
    if (rank === 3) return <Medal className="w-5 h-5 text-amber-600" />;
    return null;
  };

  const getDisplayName = (user: LeaderboardUser) => {
    if (user.firstName && user.lastName) {
      return `${user.firstName} ${user.lastName}`;
    }
    if (user.firstName) return user.firstName;
    return "Student";
  };

  const getInitials = (user: LeaderboardUser) => {
    if (user.firstName && user.lastName) {
      return `${user.firstName[0]}${user.lastName[0]}`.toUpperCase();
    }
    if (user.firstName) return user.firstName[0].toUpperCase();
    return "S";
  };

  if (isLoading || !isAuthenticated) {
    return null;
  }

  return (
    <div className="min-h-screen">
      <div className="container mx-auto px-4 py-8 max-w-4xl">
        <div className="mb-8 text-center">
          <div className="inline-flex items-center gap-2 bg-primary/10 text-primary px-4 py-2 rounded-full text-sm font-medium mb-4">
            <Trophy className="w-4 h-4" />
            Competition Leaderboard
          </div>
          <h1 className="text-4xl font-bold mb-2">Top Students</h1>
          <p className="text-muted-foreground">
            See where you stand among your peers
          </p>
        </div>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Trophy className="w-6 h-6 text-primary" />
              Rankings
            </CardTitle>
          </CardHeader>
          <CardContent className="p-0">
            {leaderboard.length === 0 ? (
              <div className="text-center py-12 text-muted-foreground">
                No students on the leaderboard yet. Be the first to study!
              </div>
            ) : (
              <div className="divide-y">
                {leaderboard.map((user, index) => (
                  <div
                    key={user.id}
                    className="flex items-center gap-4 p-4 hover-elevate cursor-pointer transition-all duration-200"
                    onClick={() => setSelectedUserId(user.id)}
                    data-testid={`leaderboard-user-${index}`}
                  >
                    {/* Rank */}
                    <div className="w-12 flex items-center justify-center">
                      {getMedalIcon(index + 1) || (
                        <span className="text-lg font-bold text-muted-foreground">
                          {index + 1}
                        </span>
                      )}
                    </div>

                    {/* Avatar and Name */}
                    <div className="flex items-center gap-3 flex-1">
                      <Avatar className="w-12 h-12">
                        <AvatarImage
                          src={user.profileImageUrl || undefined}
                          alt={getDisplayName(user)}
                          className="object-cover"
                        />
                        <AvatarFallback className="bg-primary/10 text-primary font-semibold">
                          {getInitials(user)}
                        </AvatarFallback>
                      </Avatar>
                      <span className="font-semibold text-lg" data-testid={`text-username-${index}`}>
                        {getDisplayName(user)}
                      </span>
                    </div>

                    {/* Score */}
                    <div className="flex items-center gap-6">
                      <div className="text-right">
                        <div className="text-sm text-muted-foreground">Score</div>
                        <div className="text-2xl font-bold text-primary" data-testid={`text-score-${index}`}>
                          {user.totalScore}
                        </div>
                      </div>

                      {/* Streak */}
                      <div className="flex items-center gap-2">
                        <Flame className="w-5 h-5 text-orange-500" />
                        <span className="text-xl font-bold" data-testid={`text-streak-${index}`}>
                          {user.streak}
                        </span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* User Progress Modal */}
      {selectedUserId && (
        <UserProgressModal
          userId={selectedUserId}
          onClose={() => setSelectedUserId(null)}
        />
      )}
    </div>
  );
}

