client/src/components/goal-calendar.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Calendar, Plus, Check } from "lucide-react";
import type { Goal } from "@shared/schema";
import confetti from "canvas-confetti";

interface GoalCalendarProps {
  goals: Goal[];
}

export function GoalCalendar({ goals }: GoalCalendarProps) {
  const { toast } = useToast();
  const [showAddGoal, setShowAddGoal] = useState(false);
  const [newGoalTitle, setNewGoalTitle] = useState("");
  const [newGoalType, setNewGoalType] = useState<"daily" | "weekly">("daily");

  const createGoalMutation = useMutation({
    mutationFn: async (data: { title: string; type: string; targetDate: string }) => {
      await apiRequest("POST", "/api/goals", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/goals"] });
      toast({
        title: "Goal Created!",
        description: "Your study goal has been added.",
      });
      setNewGoalTitle("");
      setShowAddGoal(false);
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create goal. Please try again.",
        variant: "destructive",
      });
    },
  });

  const completeGoalMutation = useMutation({
    mutationFn: async (goalId: string) => {
      await apiRequest("PATCH", `/api/goals/${goalId}/complete`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/goals"] });
      // Trigger confetti animation
      confetti({
        particleCount: 100,
        spread: 70,
        origin: { y: 0.6 },
      });
      toast({
        title: " Goal Completed!",
        description: "Amazing work! Keep up the great progress!",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to complete goal. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleAddGoal = () => {
    if (!newGoalTitle.trim()) {
      toast({
        title: "Error",
        description: "Please enter a goal title.",
        variant: "destructive",
      });
      return;
    }

    const targetDate = new Date();
    if (newGoalType === "weekly") {
      targetDate.setDate(targetDate.getDate() + 7);
    }

    createGoalMutation.mutate({
      title: newGoalTitle,
      type: newGoalType,
      targetDate: targetDate.toISOString().split("T")[0],
    });
  };

  const activeGoals = goals.filter((g) => !g.completed);
  const completedGoals = goals.filter((g) => g.completed);

  return (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between gap-4 space-y-0 pb-4">
        <CardTitle className="flex items-center gap-2">
          <Calendar className="w-5 h-5 text-primary" />
          Goals
        </CardTitle>
        <Button
          size="sm"
          variant="ghost"
          onClick={() => setShowAddGoal(!showAddGoal)}
          data-testid="button-toggle-add-goal"
        >
          <Plus className="w-4 h-4" />
        </Button>
      </CardHeader>
      <CardContent className="space-y-4">
        {showAddGoal && (
          <div className="space-y-3 p-4 border rounded-lg bg-muted/30">
            <Input
              placeholder="Goal title..."
              value={newGoalTitle}
              onChange={(e) => setNewGoalTitle(e.target.value)}
              data-testid="input-goal-title"
            />
            <Select value={newGoalType} onValueChange={(v: any) => setNewGoalType(v)}>
              <SelectTrigger data-testid="select-goal-type">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="daily">Daily Goal</SelectItem>
                <SelectItem value="weekly">Weekly Goal</SelectItem>
              </SelectContent>
            </Select>
            <Button
              onClick={handleAddGoal}
              disabled={createGoalMutation.isPending}
              className="w-full"
              data-testid="button-create-goal"
            >
              {createGoalMutation.isPending ? "Creating..." : "Create Goal"}
            </Button>
          </div>
        )}

        {/* Active Goals */}
        {activeGoals.length > 0 && (
          <div className="space-y-2">
            {activeGoals.map((goal) => (
              <div
                key={goal.id}
                className="flex items-start gap-3 p-3 border rounded-lg hover-elevate transition-all duration-200"
                data-testid={`goal-${goal.id}`}
              >
                <Button
                  size="icon"
                  variant="outline"
                  className="shrink-0 w-8 h-8"
                  onClick={() => completeGoalMutation.mutate(goal.id)}
                  disabled={completeGoalMutation.isPending}
                  data-testid="button-complete-goal"
                >
                  <Check className="w-4 h-4" />
                </Button>
                <div className="flex-1 min-w-0">
                  <p className="font-medium text-sm">{goal.title}</p>
                  <p className="text-xs text-muted-foreground">
                    {goal.type === "daily" ? "Daily" : "Weekly"} {" "}
                    {new Date(goal.targetDate).toLocaleDateString()}
                  </p>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Completed Goals */}
        {completedGoals.length > 0 && (
          <div className="space-y-2">
            <p className="text-xs text-muted-foreground uppercase tracking-wide font-semibold">
              Completed
            </p>
            {completedGoals.slice(0, 3).map((goal) => (
              <div
                key={goal.id}
                className="flex items-start gap-3 p-3 border rounded-lg bg-muted/30 opacity-60"
              >
                <div className="shrink-0 w-8 h-8 bg-green-500 rounded flex items-center justify-center">
                  <Check className="w-4 h-4 text-white" />
                </div>
                <div className="flex-1 min-w-0">
                  <p className="font-medium text-sm line-through">{goal.title}</p>
                  <p className="text-xs text-muted-foreground">
                    Completed {goal.completedAt && new Date(goal.completedAt).toLocaleDateString()}
                  </p>
                </div>
              </div>
            ))}
          </div>
        )}

        {activeGoals.length === 0 && completedGoals.length === 0 && (
          <div className="text-center py-8 text-muted-foreground">
            <Calendar className="w-8 h-8 mx-auto mb-2 opacity-50" />
            <p className="text-sm">No goals yet</p>
            <p className="text-xs mt-1">Set daily or weekly study goals!</p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

server/openai.ts.content:
// Using OpenRouter.ai with Llama 3.3 70B model
import OpenAI from "openai";

const openai = new OpenAI({
  apiKey: process.env.OPENROUTER_API_KEY,
  baseURL: "https://openrouter.ai/api/v1",
  defaultHeaders: {
    "Authorization": `Bearer ${process.env.OPENROUTER_API_KEY}`,
    "HTTP-Referer": process.env.REPLIT_DOMAINS || "http://localhost:5000",
    "X-Title": "Growth GPT",
  },
});

interface MCQQuestion {
  question: string;
  options: string[];
  correctAnswer: number;
}

export async function generateMCQQuestions(topic: string, subject: string): Promise<MCQQuestion[]> {
  try {
    const response = await openai.chat.completions.create({
      model: "meta-llama/llama-3.3-70b-instruct:free",
      messages: [
        {
          role: "system",
          content: `You are an expert educator creating multiple choice questions for students. Generate exactly 5 questions about the given topic. Each question should have 4 answer options (A, B, C, D) with only ONE correct answer. The wrong answers should be plausible but clearly incorrect. Format your response exactly like this:

1. Q: What is the capital of France?
   A: London
   B: Paris
   C: Berlin
   D: Madrid
   CORRECT: B

2. Q: What gas do plants absorb during photosynthesis?
   A: Oxygen
   B: Nitrogen
   C: Carbon Dioxide
   D: Hydrogen
   CORRECT: C`,
        },
        {
          role: "user",
          content: `Create 5 multiple choice questions about "${topic}" in the subject of ${subject}. Make them educational, clear, and appropriate for students. Each question must have 4 different answer options with only one correct answer.`,
        },
      ],
      max_completion_tokens: 1024,
    });

    const content = response.choices[0].message.content || "";

    // Parse the response to extract questions and answers
    const questions = parseQuestionsFromText(content, topic);

    if (questions.length === 0) {
      throw new Error("No questions were generated");
    }

    return questions;
  } catch (error: any) {
    console.error("Error generating MCQ questions:", error);
    throw new Error("Failed to generate quiz questions: " + error.message);
  }
}

function parseQuestionsFromText(text: string, topic: string): MCQQuestion[] {
  const questions: MCQQuestion[] = [];

  // Try to parse the multiple choice format
  const lines = text.split('\n');
  let currentQuestion = '';
  let currentOptions: string[] = [];
  let correctAnswer = -1;

  for (const line of lines) {
    const trimmed = line.trim();

    // Match question pattern (1. Q: or just Q:)
    if (trimmed.match(/^\d+\.\s*Q:/i) || trimmed.startsWith('Q:')) {
      // Save previous question if complete
      if (currentQuestion && currentOptions.length === 4 && correctAnswer >= 0) {
        questions.push({
          question: currentQuestion,
          options: currentOptions,
          correctAnswer: correctAnswer,
        });
      }
      // Reset for new question
      currentQuestion = trimmed.replace(/^\d+\.\s*Q:\s*/i, '').replace(/^Q:\s*/i, '').trim();
      currentOptions = [];
      correctAnswer = -1;
    }
    // Match option patterns (A:, B:, C:, D:)
    else if (trimmed.match(/^[A-D]:/i)) {
      const optionText = trimmed.replace(/^[A-D]:\s*/i, '').trim();
      currentOptions.push(optionText);
    }
    // Match correct answer pattern (CORRECT: B)
    else if (trimmed.match(/^CORRECT:\s*[A-D]/i)) {
      const correctLetter = trimmed.replace(/^CORRECT:\s*/i, '').trim().toUpperCase();
      correctAnswer = correctLetter.charCodeAt(0) - 'A'.charCodeAt(0);
    }
  }

  // Add the last question if complete
  if (currentQuestion && currentOptions.length === 4 && correctAnswer >= 0) {
    questions.push({
      question: currentQuestion,
      options: currentOptions,
      correctAnswer: correctAnswer,
    });
  }

  // If parsing failed, create fallback questions
  if (questions.length === 0) {
    return createFallbackQuestions(topic);
  }

  return questions.slice(0, 5); // Return max 5 questions
}

function createQuestion(questionText: string, correctAnswer: string): MCQQuestion {
  // Create a multiple choice question with the correct answer and 3 distractors
  const options = [correctAnswer.toLowerCase()];

  // Add simple distractors based on answer type
  const answer = correctAnswer.toLowerCase();

  // Generate context-appropriate distractors
  if (!isNaN(Number(answer))) {
    // Numeric answer - add nearby numbers
    const num = Number(answer);
    options.push(String(num + 1), String(num - 1), String(num * 2));
  } else if (answer.length <= 3) {
    // Short answer - add generic options
    options.push('yes', 'no', 'none');
  } else {
    // Text answer - add generic distractors
    options.push('unknown', 'none', 'other');
  }

  // Shuffle options and find correct answer index
  const shuffled = Array.from(new Set(options)).slice(0, 4);
  const correctIndex = shuffled.findIndex(opt => opt.toLowerCase() === answer);

  return {
    question: questionText,
    options: shuffled,
    correctAnswer: correctIndex >= 0 ? correctIndex : 0,
  };
}

function createFallbackQuestions(topic: string): MCQQuestion[] {
  // Fallback questions in case parsing fails
  return [
    {
      question: `What is the primary focus when studying ${topic}?`,
      options: ['Understanding core concepts and principles', 'Memorizing dates only', 'Learning unrelated subjects', 'Ignoring practical applications'],
      correctAnswer: 0,
    },
    {
      question: `Why is learning about ${topic} important?`,
      options: ['It builds foundational knowledge', 'It has no practical use', 'It only matters for tests', 'It should be avoided'],
      correctAnswer: 0,
    },
    {
      question: `What approach is best for mastering ${topic}?`,
      options: ['Regular practice and review', 'Cramming before exams', 'Avoiding difficult concepts', 'Skipping fundamentals'],
      correctAnswer: 0,
    },
  ];
}

client/src/components/add-session-dialog.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { insertStudySessionSchema } from "@shared/schema";
import { z } from "zod";

const formSchema = insertStudySessionSchema.extend({
  estimatedTime: z.coerce.number().min(1, "Must be at least 1 minute"),
});

type FormData = z.infer<typeof formSchema>;

interface AddSessionDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

const SUBJECTS = [
  "Mathematics",
  "Science",
  "History",
  "English",
  "Physics",
  "Chemistry",
  "Biology",
  "Computer Science",
  "Economics",
  "Psychology",
  "Other",
];

export function AddSessionDialog({ open, onOpenChange }: AddSessionDialogProps) {
  const { toast } = useToast();
  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      topic: "",
      subject: "",
      estimatedTime: 30,
      completed: false,
    },
  });

  const mutation = useMutation({
    mutationFn: async (data: FormData) => {
      await apiRequest("POST", "/api/sessions", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/sessions"] });
      toast({
        title: "Success!",
        description: "Study session created successfully.",
      });
      form.reset();
      onOpenChange(false);
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create study session. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: FormData) => {
    mutation.mutate(data);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent data-testid="dialog-add-session">
        <DialogHeader>
          <DialogTitle>Add Study Session</DialogTitle>
          <DialogDescription>
            Create a new study session. Complete it to unlock AI-generated quizzes!
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="topic"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Topic / Chapter Name</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="e.g., Photosynthesis, Algebra Basics, World War II"
                      {...field}
                      data-testid="input-topic"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="subject"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Subject</FormLabel>
                  <Select onValueChange={field.onChange} value={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-subject">
                        <SelectValue placeholder="Select a subject" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {SUBJECTS.map((subject) => (
                        <SelectItem key={subject} value={subject}>
                          {subject}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="estimatedTime"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Estimated Time (minutes)</FormLabel>
                  <FormControl>
                    <Input
                      type="number"
                      min="1"
                      placeholder="30"
                      {...field}
                      data-testid="input-estimated-time"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex gap-3 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => onOpenChange(false)}
                className="flex-1"
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={mutation.isPending}
                className="flex-1"
                data-testid="button-create-session"
              >
                {mutation.isPending ? "Creating..." : "Create Session"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

